	public function copyInto($copyObj, $deepCopy = false)
	{

		$copyObj->setUsername($this->username);

		$copyObj->setEmail($this->email);

		$copyObj->setPassword($this->password);

		$copyObj->setFullname($this->fullname);

		$copyObj->setLocation($this->location);

		$copyObj->setHomepageurl($this->homepageurl);

		$copyObj->setAbout($this->about);

		$copyObj->setAvatar($this->avatar);

		$copyObj->setTwitterId($this->twitter_id);

		$copyObj->setCheckhash($this->checkhash);

		$copyObj->setPluginsCount($this->plugins_count);

		$copyObj->setConfirmedEmail($this->confirmed_email);

		$copyObj->setAdmin($this->admin);

		$copyObj->setLoggedAt($this->logged_at);

		$copyObj->setCreatedAt($this->created_at);


		if ($deepCopy) {
			// important: temporarily setNew(false) because this affects the behavior of
			// the getter/setter methods for fkey referrer objects.
			$copyObj->setNew(false);

			foreach ($this->getPlugins() as $relObj) {
				if ($relObj !== $this) {  // ensure that we don't try to copy a reference to ourselves
					$copyObj->addPlugin($relObj->copy($deepCopy));
				}
