	/**
	 * Gets an array of Plugin objects which contain a foreign key that references this object.
	 *
	 * If this collection has already been initialized with an identical Criteria, it returns the collection.
	 * Otherwise if this Author has previously been saved, it will retrieve
	 * related Plugins from storage. If this Author is new, it will return
	 * an empty collection or the current collection, the criteria is ignored on a new object.
	 *
	 * @param      PropelPDO $con
	 * @param      Criteria $criteria
	 * @return     array Plugin[]
	 * @throws     PropelException
	 */
	public function getPlugins($criteria = null, PropelPDO $con = null)
	{
		if ($criteria === null) {
			$criteria = new Criteria(AuthorPeer::DATABASE_NAME);
		}
		elseif ($criteria instanceof Criteria)
		{
			$criteria = clone $criteria;
		}

		if ($this->collPlugins === null) {
			if ($this->isNew()) {
			   $this->collPlugins = array();
			} else {

				$criteria->add(PluginPeer::AUTHOR_ID, $this->id);

				PluginPeer::addSelectColumns($criteria);
				$this->collPlugins = PluginPeer::doSelect($criteria, $con);
			}
		} else {
			// criteria has no effect for a new object
			if (!$this->isNew()) {
				// the following code is to determine if a new query is
				// called for.  If the criteria is the same as the last
				// one, just return the collection.


				$criteria->add(PluginPeer::AUTHOR_ID, $this->id);

				PluginPeer::addSelectColumns($criteria);
				if (!isset($this->lastPluginCriteria) || !$this->lastPluginCriteria->equals($criteria)) {
					$this->collPlugins = PluginPeer::doSelect($criteria, $con);
				}
			}
		}
		$this->lastPluginCriteria = $criteria;
		return $this->collPlugins;
	}

	/**
	 * Returns the number of related Plugin objects.
	 *
	 * @param      Criteria $criteria
	 * @param      boolean $distinct
	 * @param      PropelPDO $con
	 * @return     int Count of related Plugin objects.
	 * @throws     PropelException
	 */
	public function countPlugins(Criteria $criteria = null, $distinct = false, PropelPDO $con = null)
	{
		if ($criteria === null) {
			$criteria = new Criteria(AuthorPeer::DATABASE_NAME);
		} else {
			$criteria = clone $criteria;
		}

		if ($distinct) {
			$criteria->setDistinct();
		}

		$count = null;

		if ($this->collPlugins === null) {
			if ($this->isNew()) {
				$count = 0;
			} else {

				$criteria->add(PluginPeer::AUTHOR_ID, $this->id);

				$count = PluginPeer::doCount($criteria, $con);
			}
		} else {
			// criteria has no effect for a new object
			if (!$this->isNew()) {
				// the following code is to determine if a new query is
				// called for.  If the criteria is the same as the last
				// one, just return count of the collection.


				$criteria->add(PluginPeer::AUTHOR_ID, $this->id);

				if (!isset($this->lastPluginCriteria) || !$this->lastPluginCriteria->equals($criteria)) {
					$count = PluginPeer::doCount($criteria, $con);
				} else {
					$count = count($this->collPlugins);
				}
			} else {
				$count = count($this->collPlugins);
			}
		}
		return $count;
	}

	/**
	 * Method called to associate a Plugin object to this object
	 * through the Plugin foreign key attribute.
	 *
	 * @param      Plugin $l Plugin
	 * @return     void
	 * @throws     PropelException
	 */
	public function addPlugin(Plugin $l)
	{
		if ($this->collPlugins === null) {
			$this->initPlugins();
		}
		if (!in_array($l, $this->collPlugins, true)) { // only add it if the **same** object is not already associated
			array_push($this->collPlugins, $l);
			$l->setAuthor($this);
		}
	}


	/**
	 * If this collection has already been initialized with
	 * an identical criteria, it returns the collection.
	 * Otherwise if this Author is new, it will return
	 * an empty collection; or if this Author has previously
	 * been saved, it will retrieve related Plugins from storage.
	 *
	 * This method is protected by default in order to keep the public
	 * api reasonable.  You can provide public methods for those you
	 * actually need in Author.
	 */
	public function getPluginsJoinTerm($criteria = null, $con = null, $join_behavior = Criteria::LEFT_JOIN)
	{
		if ($criteria === null) {
			$criteria = new Criteria(AuthorPeer::DATABASE_NAME);
		}
		elseif ($criteria instanceof Criteria)
		{
			$criteria = clone $criteria;
		}

		if ($this->collPlugins === null) {
			if ($this->isNew()) {
				$this->collPlugins = array();
			} else {

				$criteria->add(PluginPeer::AUTHOR_ID, $this->id);

				$this->collPlugins = PluginPeer::doSelectJoinTerm($criteria, $con, $join_behavior);
			}
		} else {
			// the following code is to determine if a new query is
			// called for.  If the criteria is the same as the last
			// one, just return the collection.

			$criteria->add(PluginPeer::AUTHOR_ID, $this->id);

			if (!isset($this->lastPluginCriteria) || !$this->lastPluginCriteria->equals($criteria)) {
				$this->collPlugins = PluginPeer::doSelectJoinTerm($criteria, $con, $join_behavior);
			}
		}
		$this->lastPluginCriteria = $criteria;

		return $this->collPlugins;
	}


